package com.example.chatapp;import android.app.Application;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.google.firebase.database.ServerValue;import com.google.firebase.database.ValueEventListener;import com.squareup.picasso.OkHttp3Downloader;import com.squareup.picasso.Picasso;import androidx.annotation.NonNull;public class ChatApp extends Application{    private DatabaseReference mUserDataRef;    private FirebaseAuth mAuth;    public void onCreate() {        super.onCreate();        Picasso.Builder builder = new Picasso.Builder(this);        builder.downloader(new OkHttp3Downloader(this,Integer.MAX_VALUE));        Picasso built = builder.build();        built.setIndicatorsEnabled(true);        Picasso.setSingletonInstance(built);        mAuth = FirebaseAuth.getInstance();        if(mAuth.getCurrentUser() != null) {            mUserDataRef = FirebaseDatabase.getInstance().getReference().child("Users").child(mAuth.getCurrentUser().getUid());            mUserDataRef.addValueEventListener(new ValueEventListener() {                @Override                public void onDataChange(@NonNull DataSnapshot dataSnapshot) {                    mUserDataRef.child("online").onDisconnect().setValue(ServerValue.TIMESTAMP);                    mUserDataRef.child("online").setValue("true");                }                @Override                public void onCancelled(@NonNull DatabaseError databaseError) {                }            });        }    }}